import React, { useState } from 'react'
import { loginUser, signupUser } from '../Services/authService';


const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    fullname: '',
    email: '',
    password: '',
  });

  const toggleLogin = () => setIsLogin(!isLogin);

  // Input changes 
  const handleChange = (e) => {
    setFormData({
      ...formData, 
      [e.target.name]: e.target.value
    })
  }

  // Submit 
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLogin) {
      const response = await loginUser(formData.email, formData.password);
      console.log('Login response: ', response);
    } else {
      const response = await signupUser(formData);
      console.log('Signup response response: ', response);
    }
  }

  return (
    <div className='container'>
      <div className='header'>
        <div className='text'>{isLogin ? "Login" : "Signup"}</div>
        <div className='underline'></div>
      </div>
      <form onSubmit={handleSubmit}>
        <div className='input'>
          {!isLogin && (
            <>
              {/* Username */}
              <div className='input'>
                <label htmlFor="username" className='form-label'>Username:</label>              
                <input 
                  type='text' 
                  name="username" 
                  placeholder="Username"
                  className='form-control' 
                  value={formData.username} 
                  onChange={handleChange}
                />
              </div>
              {/* First name */}
              <div className='input'>
                <label htmlFor='fullname' className='form-label'>Fullname:</label>                         
                <input 
                  type='text' 
                  name="fullname" 
                  placeholder="Full name" 
                  className='form-control'
                  value={formData.fullname} 
                  onChange={handleChange}
                />
              </div>
            </>
          )}

          {/* Email */}
          <div className='input'>
            <label htmlFor="email" className='form-label'>Email:</label>              
            <input 
              type='email' 
              name="email" 
              placeholder="Email" 
              className='form-control'
              value={formData.email} 
              onChange={handleChange}
            />
          </div>
          {/* Password */}
          <div className='input'>
            <label htmlFor="password" className='form-label'>Password:</label>              
            <input 
              type='password' 
              name="password" 
              placeholder="Password" 
              className='form-control'
              value={formData.password} 
              onChange={handleChange}
            />
          </div>
          <div className="forgot-password">Forget Password? <span>Click here</span></div>
          <button type='submit'>{isLogin ? "Login" : "Signup"}</button>
        </div>
        <p className='text-muted mt-3'>
          {isLogin ? "Don't have an account?" : "Already have an account?"}
          <span onClick={toggleLogin} style={{cursor: 'pointer', color:'blue'}}> {isLogin ? "Signup" : "Login"}</span>
        </p>
      </form>
    </div>
  )
}

export default AuthPage
