import React from "react";
import './App.css';

export default class TodoItem extends React.Component {
    render() {
        let {title, description, completed} = this.props;

        return (
            <div className="todo-item p-3 mb-5 d-flex justify-content-between rounded">
                <div> 
                    <h5>{title}</h5>
                    <p>{(!description) ? 'default text': description}</p>
                </div>

                <button className="btn btn-success">{(completed === true) ? 'Completed': 'Not Completed'}</button>
            </div>
        );
    }
}
