import React from "react";
import TodoItem from "./TodoItem";
import './App.css';

export default class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            todoItems: [
                {title: 'Item 1', description: '', completed: true},
                {title: 'Item 2', description: '', completed: false},
                {title: 'Item 3', description: '', completed: false},
            ]
        };
    }

    toggleComplete(index) {
        let todoItems = this.state.todoItems.slice();
        todoItems[index].completed = !todoItems.completed[index].completed

    }

    render() {
        let items = this.state.todoItems.map((item, index) => (
            <TodoItem
                title={item.title}
                description={item.description}
                completed={item.completed}
                onToggleComplete={() => this.toggleComplete(index)}
            />
        ));
        return (
        <div className="container">
            <div className="mt-2 mb-5">Todo creation</div>
            <div>
                {items}
            </div>
        </div>
        );
    }
}
